sys_script_include.script:
var diminuirUm = Class.create();
diminuirUm.prototype = {
    initialize: function() {
    },

    atualizarAvailableSeat: function(eventId, quantidade) {
        var atualizarCampoAvailable_Seats = new GlideRecord("x_1093980_event_0_event");

        // Busca o registro específico do evento pelo ID
        atualizarCampoAvailable_Seats.addQuery('sys_id', eventId);
        atualizarCampoAvailable_Seats.query(); // Executa a consulta

        // Verifica se algum registro foi encontrado
        if (atualizarCampoAvailable_Seats.next()) {
            // Verifica se há lugares disponíveis suficientes
            if (atualizarCampoAvailable_Seats.available_seats >= quantidade) {
                // Diminui a quantidade de lugares disponíveis
                atualizarCampoAvailable_Seats.available_seats -= quantidade;
                atualizarCampoAvailable_Seats.update(); // Salva as alterações
                return true; // Retorna sucesso
            } else {
                return false; // Não há lugares suficientes
            }
        } else {
            return false; // Registro não encontrado
        }
    },

    type: 'diminuirUm'
};
